import React from 'react';
import styles from './App.module.scss';
import Input from './components/Input/Input';
import Board from './Board/Board';
import Button from './components/Button/Button';
import produce from 'immer/dist/immer';

class App extends React.PureComponent {
	state = {
		cities: {
			items: ['Oaxaca'],
			index: 0,
			input:''
		},
		drinks: {
			items: ['Cerveza'],
			index: 0,
			input:''
		},
		friends: {
			items: ['Sonia'],
			index: 0,
			input:''
		}

	};

	onHandleButton = (object) => {
		const nextState = produce(this.state, (draft) => {
			if (draft[object].items.length > draft[object].index + 1) draft[object].index = draft[object].index + 1;
			else draft[object].index = 0;
		});
		this.setState(nextState);
	};

	onAddButtonClick = (object) => {
		//console.log ('boton:',event.target);
		const nextState = produce(this.state, (draft) => {
			draft[object].items = draft[object].items.concat(draft[object].input);
			draft[object].input = '';
			console.log('input',draft[object].input);
		});
		this.setState(nextState);
	};

	onDeleteButtonClick = (object) => {
		const nextState = produce(this.state, (draft) => {
			draft[object].items.splice(draft[object].input-1, 1);
			console.log(draft[object].items)
		});
		this.setState(nextState);
	};

	onInputChange = (event,object) => {
		//let { value } = event.target.value;
		console.log('TCL: App -> onInputChange -> value', event.target.value);
		console.log('object',object);
		const nextState = produce(this.state, (draft) => {
			draft[object].input = event.target.value;
		});

		this.setState(nextState);
	};

	render() {
		const { cities, drinks, friends } = this.state;
		return (
			<div>
				<p className={styles.title}>¡Bienvenidos al curso de programación de cómputo móvil!</p>
				<div className={styles.container_boards}>
					<div className={styles.container_add}>
						<Input value={cities.input} name="ciudades" onChange={(event) => this.onInputChange(event,'cities')} />
						<Button label={'+ Cities'} name="add" onClick={(input) => this.onAddButtonClick('cities')} />
						<Button label={'- Cities'} name="delete" onClick={() => this.onDeleteButtonClick('cities')} />
					</div>

					<div className={styles.container_add}>
						<Input value={drinks.input} name="bebidas" onChange={(event) => this.onInputChange(event,'drinks')} />
						<Button label={'+ Drinks'} onClick={() => this.onAddButtonClick('drinks')} />
						<Button label={'- Drinks'} onClick={() => this.onDeleteButtonClick('drinks')} />
					</div>

					<div className={styles.container_add}>
						<Input value={friends.input} name="amigos" onChange={(event) => this.onInputChange(event,'friends')} />
						<Button label={'+ Friends'} onClick={() => this.onAddButtonClick('friends')} />
						<Button label={'- Friends'} onClick={() => this.onDeleteButtonClick('friends')} />
					</div>
				</div>	
				<div className={styles.container_boards}>
					<Board items={cities.items} index={cities.index} label={'Siguiente'} onButtonClick={() => this.onHandleButton('cities')} />
					<Board items={drinks.items} index={drinks.index} label={'Siguiente'} onButtonClick={() => this.onHandleButton('drinks')} />
					<Board items={friends.items} index={friends.index} label={'Siguiente'} onButtonClick={() => this.onHandleButton('friends')} />

				</div>
				{/*
					<p className={styles.result}>
						Resultado: <br />
						<label> {family.items[family.index]} </label>
						<br />
						<label> {sports.items[sports.index]} </label>
						<br />
						<label> {numbers.items[numbers.index]} </label>
						<br />
						<label> {drinks.items[drinks.index]} </label>
						<br />
					</p>*/
				}
			</div>
		);
	}
}

export default App;
